import React, { useState } from "react";
import { Bar } from "react-chartjs-2";

const BarGraph = () => {
  const [data, setData] = useState({
    labels: ["A", "B", "C", "D", "E"],
    datasets: [
      {
        label: "Bar Graph",
        backgroundColor: "rgba(75,192,192,0.6)",
        borderColor: "rgba(75,192,192,1)",
        borderWidth: 1,
        hoverBackgroundColor: "rgba(75,192,192,0.8)",
        hoverBorderColor: "rgba(75,192,192,1)",
        data: [50, 30, 70, 45, 60], // Initial random values
      },
    ],
  });

  const increaseHeight = () => {
    // Increase the height of the bars
    const newData = { ...data };
    newData.datasets[0].data = data.datasets[0].data.map((value) => value + 10);
    setData(newData);
  };

  const decreaseHeight = () => {
    // Decrease the height of the bars
    const newData = { ...data };
    newData.datasets[0].data = data.datasets[0].data.map((value) => value - 10);
    setData(newData);
  };

  return (
    <div>
      <h2>Bar Graph</h2>
      <Bar data={data} />
      <div>
        <button onClick={increaseHeight}>Increase Height</button>
        <button onClick={decreaseHeight}>Decrease Height</button>
      </div>
    </div>
  );
};

export default BarGraph;
