import React, { useState, useEffect } from "react";
import ReactApexChart from "react-apexcharts";
import axios from "axios";

const ApexChart = () => {
  const [serverData, setServerData] = useState({});
  const [barHeight, setBarHeight] = useState(350); // Initial height of the bar graph

  async function fetchData() {
    try {
      const response = await axios.get("http://localhost:5000/optimize");
      console.log("Response data:", response.data);
      setServerData(response.data);
    } catch (error) {
      console.error("Error fetching data from the server:", error);
    }
  }

  useEffect(() => {
    fetchData(); // Call the fetchData function when the component mounts
  }, []);

  // ... Rest of your code ...

  const increaseBarHeight = () => {
    // Generate a random value to increase the height (you can adjust the range)
    const randomIncrease = Math.floor(Math.random() * 100);
    setBarHeight(barHeight + randomIncrease);
  };

  return (
    <div>
      <div id="chart">
        <ReactApexChart options={options} series={series} type="bar" height={barHeight} />
      </div>
      <button onClick={increaseBarHeight}>Increase Height</button>
    </div>
  );
};

export default ApexChart;
